import React from 'react';
import { useAuth0 } from '@auth0/auth0-react';

const LoginButton = () => {
  const domain = process.env.REACT_APP_AUTH0_DOMAIN
  const clientId = process.env.REACT_APP_AUTH0_CLIENTID
  const { loginWithRedirect } = useAuth0();
  return (
    <button
      className="btn btn-primary btn-block"
      onClick={(e) => {
        e.preventDefault();
        window.location.href = 'https://dev-royzhu.us.auth0.com/authorize?audience=boardgameforum&response_type=token&client_id=wRJEcBy466MQx6VeQzzem1kSYs3CxpCG&redirect_uri=http://localhost:3000/logout-result';
      }}
    >
      Log In
    </button>
  );
};

export default LoginButton;
