import React, { Component } from 'react';
import $ from 'jquery';

import '../css/FormView.css';

class FormView extends Component {
  constructor(props){
    super(props);
    this.state = {
      subject: "",
      content: ""
    }
  }


  submitPost = (event) => {
    event.preventDefault();
    $.ajax({
      url: '/post', //TODO: update request URL
      type: "POST",
      dataType: 'json',
      contentType: 'application/json',
      data: JSON.stringify({
        subject: this.state.subject,
        content: this.state.content
      }),
      xhrFields: {
        withCredentials: true
      },
      crossDomain: true,
      success: (result) => {
        document.getElementById("create-post-form").reset();
      },
      error: (error) => {
        alert('Unable to create post. Please try your request again')
      }
    })
  }

  handleChange = (event) => {
    this.setState({[event.target.name]: event.target.value})
  }

  render() {
    return (
      <div id="add-form">
        <h2>Create A New Post</h2>
        <form className="form-view" id="create-post-form" onSubmit={this.submitQuestion}>
          <label>
            Subject:<br></br>
          </label>
          <input type="text" name="subject" onChange={this.handleChange}/>
          <label>
            Summary:<br></br>
          </label>
          <input type="text" name="content" onChange={this.handleChange}/>
          <input type="submit" className="button" value="Submit" />
        </form>
      </div>
    );
  }
}

export default FormView;
