import React, { Component } from 'react';
import '../css/Post.css';

class Post extends Component {
  constructor(props){
    super(props);
    this.state = {
      deletable: false
    }
  }

  flipVisibility() {
    this.setState({deletable: !this.state.deletable});
  }

  render() {
    const { subject, content} = this.props;
    return (
      <div className="Post-holder">
        <div className="Post">{subject}</div>
        <div className="Post-status">
            {this.state.deletable ? 'Hide' : 'Show'}
            <img style={{"visibility": this.state.visibleAnswer ? 'visible' : 'hidden'}} src="delete.png" className="delete" onClick={() => this.props.postAction('DELETE')}/>
        </div>
        <div className="summary-holder">
          <span >Summary: {content}</span>
        </div>
      </div>
    );
  }
}

export default Post;
