import React, { Component } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import '../css/Post.css';


class Replyline extends Component {
  constructor(props){
    super(props);
  }

  render() {
    const {subject} = this.props;
    const { isAuthenticated } = useAuth0();
    return (
      <div className="Reply-holder">
        <div className="Reply">{subject}</div>
        <div className="Post-status">
          <img alt="deleted" style={{ "visibility": isAuthenticated ? 'visible' : 'hidden'}} src="delete.png" className="delete" onClick={() => this.props.deleteAction('DELETE')}/>
        </div>
      </div>
    );
  }
}

export default Replyline;
